function GPDfixNumbers(text,local){if(text===null){return null;};if(local==='fa_IR'){text=text.replace(/0/g,'\u06F0');text=text.replace(/1/g,'\u06F1');text=text.replace(/2/g,'\u06F2');text=text.replace(/3/g,'\u06F3');text=text.replace(/4/g,'\u06F4');text=text.replace(/5/g,'\u06F5');text=text.replace(/6/g,'\u06F6');text=text.replace(/7/g,'\u06F7');text=text.replace(/8/g,'\u06F8');text=text.replace(/9/g,'\u06F9');};return text;};function GPDupdateClock(){var currentTime=new Date();var currentHours=currentTime.getHours();var currentMinutes=currentTime.getMinutes();currentHours=(currentHours<10?"0":"")+currentHours;currentMinutes=(currentMinutes<10?"0":"")+currentMinutes;var currentTimeString=GPDfixNumbers(currentHours+":"+currentMinutes,GPD_clock['local']);jQuery("#gpd-now").html(currentTimeString);};jQuery(document).ready(function($){setInterval('GPDupdateClock()',60*1000);});